mapper.select:
SELECT ticket.*,project.project_name,ticket_type.`name` as type_name,`user`.`name` as user_name FROM ticket LEFT JOIN project ON ticket.project_id = project.id LEFT JOIN ticket_type ON ticket.ticket_type = ticket_type.id LEFT JOIN `user` ON ticket.create_user = `user`.id WHERE 1=1
AND project.project_name LIKE '%' #{projectName} '%'
limit #{currIndex} , #{pageSize}
